import React from 'react';
import ProfessionalParticles from '../Background/ProfessionalParticles';
// Other imports commented out
// import FallingStars from '../Background/FallingStars';
// import ParticlesBackground from '../Background/ParticlesBackground';
// import GradientBackground from '../Background/GradientBackground';

const Hero = () => {
  return (
    <section id="home" className="relative flex flex-col md:flex-row items-center justify-between py-16 px-6 md:px-12 lg:px-24 bg-gradient-to-r from-indigo-900 to-blue-900 min-h-screen overflow-hidden">
      {/* Professional particles background */}
      <ProfessionalParticles />

      {/* Content (with z-index to place above animation) */}
      <div className="w-full md:w-1/2 mb-10 md:mb-0 z-10">
        <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-4">
          Hi, I'm <span className="text-blue-300">Subashini S</span>
        </h1>
        <h2 className="text-2xl md:text-3xl font-semibold text-blue-100 mb-6">
          Aspiring Software Engineer
        </h2>
        <p className="text-lg text-gray-200 mb-8 max-w-lg">
          I create scalable applications with Java, Spring Boot, React, and MongoDB.
          Passionate about optimizing backend performance and enhancing user experience.
        </p>
        <div className="flex flex-wrap gap-4">
          <a 
            href="#contact" 
            className="px-6 py-3 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition duration-300 transform hover:-translate-y-1"
          >
            Contact Me
          </a>
          <a 
            href="#projects" 
            className="px-6 py-3 border border-blue-300 text-blue-100 rounded-md hover:bg-blue-800 hover:border-blue-200 transition duration-300 transform hover:-translate-y-1"
          >
            View Projects
          </a>
        </div>
      </div>
      <div className="w-full md:w-1/2 flex justify-center z-10">
        <div className="relative w-64 h-64 md:w-80 md:h-80 rounded-full overflow-hidden border-4 border-blue-300 shadow-xl">
          <img 
            src="/assets/images/profile.jpg" 
            alt="Subashini S" 
            className="absolute inset-0 w-full h-full object-cover"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = 'https://via.placeholder.com/300?text=Subashini+S';
            }}
          />
        </div>
      </div>
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce z-10">
        <a href="#about" className="text-blue-200 hover:text-white">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
          </svg>
        </a>
      </div>
    </section>
  );
};

export default Hero;
