import React, { useEffect, useRef } from 'react';

const FallingStars = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let starsArray = [];
    
    // Set canvas size
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    
    window.addEventListener('resize', handleResize);
    handleResize();
    
    // Star class
    class Star {
      constructor() {
        this.reset();
      }
      
      reset() {
        this.x = Math.random() * canvas.width;
        this.y = 0;
        this.size = Math.random() * 2 + 1;
        this.speed = Math.random() * 3 + 1;
        this.tailLength = Math.random() * 80 + 20;
        this.color = `rgba(255, 255, 255, ${Math.random() * 0.7 + 0.3})`;
      }
      
      update() {
        this.y += this.speed;
        this.x += this.speed * 0.3; // slight horizontal movement
        
        // Reset if off screen
        if (this.y > canvas.height || this.x > canvas.width) {
          this.reset();
        }
      }
      
      draw() {
        ctx.strokeStyle = this.color;
        ctx.lineWidth = this.size;
        ctx.beginPath();
        ctx.moveTo(this.x, this.y);
        ctx.lineTo(this.x - this.tailLength * 0.3, this.y - this.tailLength);
        ctx.stroke();
        
        // Add a small glow at the head of the star
        ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size + 1, 0, Math.PI * 2);
        ctx.fill();
      }
    }
    
    // Create stars
    const createStars = () => {
      const starCount = Math.floor(canvas.width / 100); // Adjust density
      for (let i = 0; i < starCount; i++) {
        const star = new Star();
        star.y = Math.random() * canvas.height; // Distribute initially across screen
        starsArray.push(star);
      }
    };
    
    createStars();
    
    // Animation loop
    const animate = () => {
      // Create fade effect by using semi-transparent background
      ctx.fillStyle = 'rgba(10, 10, 35, 0.1)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      
      for (let i = 0; i < starsArray.length; i++) {
        starsArray[i].update();
        starsArray[i].draw();
      }
      
      // Add a new star occasionally
      if (Math.random() < 0.03) {
        starsArray.push(new Star());
      }
      
      // Remove excess stars
      if (starsArray.length > 30) {
        starsArray.shift();
      }
      
      requestAnimationFrame(animate);
    };
    
    animate();
    
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <canvas 
      ref={canvasRef} 
      className="absolute top-0 left-0 w-full h-full pointer-events-none z-0"
    />
  );
};

export default FallingStars;
