import React, { useEffect, useRef, useState } from 'react';

const Hero = () => {
  const canvasRef = useRef(null);
  const [animationType, setAnimationType] = useState('particles'); // 'particles', 'gradient', 'waves', or 'combined'

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return; // Add null check for safety
    
    const ctx = canvas.getContext('2d');
    let particlesArray = [];
    let animationFrameId;
    
    // Colors for dark theme
    const colors = ['#2563eb', '#4f46e5', '#7c3aed', '#8b5cf6'];
    
    // Particle class
    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 2 + 0.5; // Smaller particles for subtlety
        this.color = colors[Math.floor(Math.random() * colors.length)];
        this.speedX = Math.random() * 0.3 - 0.15;
        this.speedY = Math.random() * 0.3 - 0.15;
        this.opacity = Math.random() * 0.5 + 0.1;
      }
      
      update() {
        // Gentle movement
        this.x += this.speedX;
        this.y += this.speedY;
        
        // Wrap around edges
        if (this.x < 0) this.x = canvas.width;
        if (this.x > canvas.width) this.x = 0;
        if (this.y < 0) this.y = canvas.height;
        if (this.y > canvas.height) this.y = 0;
      }
      
      draw() {
        ctx.fillStyle = this.color;
        ctx.globalAlpha = this.opacity;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.closePath();
        ctx.fill();
      }
    }

    // Wave class for wave animation
    class Wave {
      constructor(canvas) {
        this.canvas = canvas;
        this.waveWidth = canvas.width;
        this.waveHeight = canvas.height;
        this.segments = 10;
        this.wavelength = this.waveWidth / this.segments;
        this.amplitude = Math.random() * 20 + 10;
        this.speed = Math.random() * 0.05 + 0.01;
        this.offset = Math.random() * 100;
        this.color = colors[Math.floor(Math.random() * colors.length)];
        this.opacity = Math.random() * 0.2 + 0.05;
        this.y = Math.random() * this.waveHeight * 0.8 + this.waveHeight * 0.1;
      }

      draw(ctx) {
        ctx.strokeStyle = this.color;
        ctx.globalAlpha = this.opacity;
        ctx.lineWidth = 1;
        ctx.beginPath();

        for (let x = 0; x <= this.waveWidth; x += 5) {
          const y = this.y + Math.sin(x * this.speed + this.offset) * this.amplitude;
          if (x === 0) {
            ctx.moveTo(x, y);
          } else {
            ctx.lineTo(x, y);
          }
        }

        ctx.stroke();
      }

      update() {
        this.offset += 0.05;
      }
    }
    
    // Set canvas size
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      
      if (animationType === 'particles' || animationType === 'combined') {
        initParticles();
      }
      
      if (animationType === 'waves' || animationType === 'combined') {
        initWaves();
      }
    };
    
    // Create particles
    function initParticles() {
      particlesArray = [];
      // Adjust number of particles based on screen size
      const particleCount = Math.min(Math.floor((canvas.width * canvas.height) / 15000), 100);
      for (let i = 0; i < particleCount; i++) {
        particlesArray.push(new Particle());
      }
    }

    // Initialize waves
    let waves = [];
    function initWaves() {
      waves = [];
      const waveCount = 5;
      for (let i = 0; i < waveCount; i++) {
        waves.push(new Wave(canvas));
      }
    }
    
    // Connect particles with lines if they're close
    function connectParticles() {
      const maxDistance = canvas.width > 768 ? 150 : 100;
      for (let a = 0; a < particlesArray.length; a++) {
        for (let b = a; b < particlesArray.length; b++) {
          const dx = particlesArray[a].x - particlesArray[b].x;
          const dy = particlesArray[a].y - particlesArray[b].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          
          if (distance < maxDistance) {
            // More subtle connections
            const opacity = 0.2 * (1 - distance / maxDistance);
            ctx.strokeStyle = `rgba(99, 102, 241, ${opacity})`;
            ctx.lineWidth = 0.5;
            ctx.beginPath();
            ctx.moveTo(particlesArray[a].x, particlesArray[a].y);
            ctx.lineTo(particlesArray[b].x, particlesArray[b].y);
            ctx.stroke();
          }
        }
      }
    }
    
    // Draw gradient background
    function drawGradient(time) {
      const gradient = ctx.createLinearGradient(0, 0, canvas.width, canvas.height);
      
      // Shift hue values based on time for subtle animation
      const hueShift = (Math.sin(time * 0.001) + 1) * 10; 
      
      gradient.addColorStop(0, `hsl(220, 75%, 15%)`);
      gradient.addColorStop(0.5, `hsl(${240 + hueShift}, 70%, 20%)`);
      gradient.addColorStop(1, `hsl(260, 65%, 25%)`);
      
      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    }
    
    // Animation loop
    function animate(timestamp) {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      if (animationType === 'gradient' || animationType === 'combined') {
        drawGradient(timestamp);
      }

      if (animationType === 'waves' || animationType === 'combined') {
        waves.forEach(wave => {
          wave.update();
          wave.draw(ctx);
        });
      }
      
      if (animationType === 'particles' || animationType === 'combined') {
        // Draw particles and connections
        for (let i = 0; i < particlesArray.length; i++) {
          particlesArray[i].update();
          particlesArray[i].draw();
        }
        
        connectParticles();
      }
      
      animationFrameId = requestAnimationFrame(animate);
    }
    
    window.addEventListener('resize', handleResize);
    handleResize(); // Call handleResize after all functions are defined
    
    if (animationType === 'waves' || animationType === 'combined') {
      initWaves();
    }
    
    if (animationType === 'particles' || animationType === 'combined') {
      initParticles();
    }
    
    animate();
    
    // Clean up
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(animationFrameId);
    };
  }, [animationType]);

  return (
    <section id="home" className="relative flex flex-col md:flex-row items-center justify-between py-16 px-6 md:px-12 lg:px-24 min-h-screen overflow-hidden">
      {/* Background elements */}
      <div className="absolute inset-0 z-0 bg-gradient-to-r from-gray-900 to-indigo-950 animate-gradientShift bg-[length:400%_400%]" />
      
      {/* Canvas for animations */}
      <canvas
        ref={canvasRef}
        className="absolute inset-0 w-full h-full z-0"
        style={{ opacity: 0.7 }}
      />

      {/* Floating Background Shapes - for additional depth */}
      <div className="absolute inset-0 overflow-hidden z-0">
        <div className="absolute top-[10%] left-[15%] w-64 h-64 rounded-full bg-indigo-900/20 blur-[50px] animate-float-slow"></div>
        <div className="absolute bottom-[20%] right-[10%] w-80 h-80 rounded-full bg-blue-900/20 blur-[60px] animate-float-medium"></div>
        <div className="absolute top-[40%] right-[20%] w-40 h-40 rounded-full bg-purple-900/20 blur-[40px] animate-float-fast"></div>
      </div>

      {/* Content (with z-index to place above animation) */}
      <div className="w-full md:w-1/2 mb-10 md:mb-0 z-10">
        <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-4">
          Hi, I'm <span className="text-indigo-400 relative">
            Subashini S
            <span className="absolute bottom-0 left-0 w-full h-0.5 bg-indigo-400/50"></span>
          </span>
        </h1>
        <h2 className="text-2xl md:text-3xl font-semibold text-indigo-200 mb-6">
          Aspiring Software Engineer
        </h2>
        <p className="text-lg text-gray-300 mb-8 max-w-lg">
          I create scalable applications with Java, Spring Boot, React, and MongoDB.
          Passionate about optimizing backend performance and enhancing user experience.
        </p>
        <div className="flex flex-wrap gap-4">
          <a 
            href="#contact" 
            className="px-6 py-3 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition duration-300 transform hover:-translate-y-1 hover:shadow-lg hover:shadow-indigo-500/20"
          >
            Contact Me
          </a>
          <a 
            href="#projects" 
            className="px-6 py-3 border border-indigo-400 text-indigo-200 rounded-md hover:bg-indigo-900 hover:border-indigo-300 transition duration-300 transform hover:-translate-y-1 hover:shadow-lg hover:shadow-indigo-500/10"
          >
            View Projects
          </a>
        </div>

        {/* Animation controls - remove this in production if not needed */}
        <div className="mt-8 flex flex-wrap gap-2">
          {['particles', 'gradient', 'waves', 'combined'].map((type) => (
            <button 
              key={type}
              onClick={() => setAnimationType(type)}
              className={`text-xs px-3 py-1 rounded-full transition-colors ${
                animationType === type 
                  ? 'bg-indigo-600 text-white' 
                  : 'bg-gray-800/50 text-gray-300 hover:bg-gray-700/50'
              }`}
            >
              {type.charAt(0).toUpperCase() + type.slice(1)}
            </button>
          ))}
        </div>
      </div>
      <div className="w-full md:w-1/2 flex justify-center z-10">
        <div className="relative w-64 h-64 md:w-80 md:h-80 rounded-full overflow-hidden border-4 border-indigo-400 shadow-xl shadow-indigo-500/20 animate-pulse-slow">
          <img 
