import React, { useEffect, useRef } from 'react';

const ProfessionalParticles = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let particlesArray = [];
    
    const colors = ['#8CB9FF', '#A2A1FF', '#C6A4FF', '#FFC9F3'];
    
    // Set canvas size
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      init(); // Reinitialize particles on resize
    };
    
    window.addEventListener('resize', handleResize);
    handleResize();
    
    // Mouse interaction
    let mouse = {
      x: null,
      y: null,
      radius: 150
    };
    
    window.addEventListener('mousemove', (event) => {
      mouse.x = event.x;
      mouse.y = event.y;
    });
    
    // Particle class
    class Particle {
      constructor(x, y, size, color, weight) {
        this.x = x;
        this.y = y;
        this.size = size;
        this.color = color;
        this.weight = weight;
        this.directionX = Math.random() * 2 - 1;
        this.directionY = Math.random() * 2 - 1;
        this.initialX = x;
        this.initialY = y;
      }
      
      draw() {
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fillStyle = this.color;
        ctx.fill();
      }
      
      update() {
        // Check for mouse proximity
        if (mouse.x != null && mouse.y != null) {
          let dx = mouse.x - this.x;
          let dy = mouse.y - this.y;
          let distance = Math.sqrt(dx * dx + dy * dy);
          
          if (distance < mouse.radius) {
            const forceDirectionX = dx / distance;
            const forceDirectionY = dy / distance;
            const force = (mouse.radius - distance) / mouse.radius;
            
            const dirX = forceDirectionX * force * this.weight;
            const dirY = forceDirectionY * force * this.weight;
            
            this.x -= dirX;
            this.y -= dirY;
          } else {
            // Return to original position
            if (this.x !== this.initialX) {
              let dx = this.initialX - this.x;
              this.x += dx / 20;
            }
            if (this.y !== this.initialY) {
              let dy = this.initialY - this.y;
              this.y += dy / 20;
            }
          }
        }
        
        // Gentle flowing motion
        this.x += this.directionX * 0.2;
        this.y += this.directionY * 0.2;
        
        // Boundary check and reverse direction
        if (this.x > canvas.width || this.x < 0) {
          this.directionX *= -1;
        }
        if (this.y > canvas.height || this.y < 0) {
          this.directionY *= -1;
        }
        
        this.draw();
      }
    }
    
    // Initialize particles
    function init() {
      particlesArray = [];
      const numberOfParticles = Math.min(Math.floor((canvas.width * canvas.height) / 9000), 100);
      
      for (let i = 0; i < numberOfParticles; i++) {
        const size = Math.random() * 2 + 1;
        const x = Math.random() * canvas.width;
        const y = Math.random() * canvas.height;
        const color = colors[Math.floor(Math.random() * colors.length)];
        const weight = (Math.random() * 2) + 0.1;
        
        particlesArray.push(new Particle(x, y, size, color, weight));
      }
    }
    
    // Connect close particles with lines
    function connect() {
      const maxDistance = 200;
      for (let a = 0; a < particlesArray.length; a++) {
        for (let b = a; b < particlesArray.length; b++) {
          const dx = particlesArray[a].x - particlesArray[b].x;
          const dy = particlesArray[a].y - particlesArray[b].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          
          if (distance < maxDistance) {
            const opacity = 1 - (distance / maxDistance);
            ctx.strokeStyle = `rgba(140, 185, 255, ${opacity * 0.5})`;
            ctx.lineWidth = 0.5;
            ctx.beginPath();
            ctx.moveTo(particlesArray[a].x, particlesArray[a].y);
            ctx.lineTo(particlesArray[b].x, particlesArray[b].y);
            ctx.stroke();
          }
        }
      }
    }
    
    // Animation loop
    function animate() {
      requestAnimationFrame(animate);
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      particlesArray.forEach(particle => particle.update());
      connect();
    }
    
    init();
    animate();
    
    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('mousemove', handleResize);
    };
  }, []);

  return (
    <canvas 
      ref={canvasRef} 
      className="absolute top-0 left-0 w-full h-full pointer-events-none opacity-70 z-0"
    />
  );
};

export default ProfessionalParticles;
