import React, { useEffect, useRef } from 'react';

const ScrollReveal = ({ children, threshold = 0.1, delay = 0, direction = 'up' }) => {
  const elementRef = useRef(null);

  useEffect(() => {
    const currentElement = elementRef.current;
    
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setTimeout(() => {
              currentElement.classList.add('animate');
            }, delay);
            observer.unobserve(currentElement);
          }
        });
      },
      { threshold }
    );

    if (currentElement) {
      observer.observe(currentElement);
    }

    return () => {
      if (currentElement) {
        observer.unobserve(currentElement);
      }
    };
  }, [threshold, delay]);

  let initialClassName = 'opacity-0 transition duration-700 ease-out';
  if (direction === 'up') initialClassName += ' translate-y-10';
  if (direction === 'down') initialClassName += ' -translate-y-10';
  if (direction === 'left') initialClassName += ' translate-x-10';
  if (direction === 'right') initialClassName += ' -translate-x-10';
  if (direction === 'scale') initialClassName += ' scale-95';

  return (
    <div 
      ref={elementRef} 
      className={initialClassName}
      style={{ willChange: 'opacity, transform' }}
    >
      {children}
      <style jsx>{`
        .animate {
          opacity: 1 !important;
          transform: translateY(0) translateX(0) scale(1) !important;
        }
      `}</style>
    </div>
  );
};

export default ScrollReveal;
