import React, { useState } from 'react';
import { projects, projectCategories } from '../../data/projects';
import ProjectCard from './ProjectCard';
import ScrollReveal from '../Animations/ScrollReveal';

const Projects = () => {
  const [category, setCategory] = useState("All");

  const filteredProjects = category === "All" 
    ? projects 
    : projects.filter(project => project.technologies.includes(category));

  return (
    <section id="projects" className="py-20 px-6 md:px-12 lg:px-24 bg-gray-50 relative">
      {/* Subtle Floating Elements */}
      <div className="absolute inset-0 overflow-hidden z-0">
        <div className="absolute w-full h-full">
          {Array.from({ length: 6 }).map((_, i) => (
            <div
              key={i}
              className="absolute rounded-full opacity-10"
              style={{
                top: `${Math.random() * 100}%`,
                left: `${Math.random() * 100}%`,
                width: `${Math.random() * 100 + 50}px`,
                height: `${Math.random() * 100 + 50}px`,
                background: i % 2 === 0 ? '#3B82F6' : '#6366F1',
                animation: `float ${Math.random() * 10 + 10}s infinite alternate ease-in-out ${Math.random() * 5}s`
              }}
            ></div>
          ))}
        </div>
      </div>
      
      <div className="max-w-6xl mx-auto relative z-10">
        <ScrollReveal>
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">My Projects</h2>
            <div className="w-20 h-1 bg-blue-600 mx-auto mb-6"></div>
            <p className="text-gray-600 max-w-2xl mx-auto">
              Here are some of my recent projects. Each project is a unique challenge that I've approached with creativity and technical expertise.
            </p>
          </div>
        </ScrollReveal>

        {/* Project Categories Filter */}
        <ScrollReveal direction="up" delay={200}>
          <div className="flex flex-wrap justify-center gap-3 mb-12">
            {projectCategories.map((cat, index) => (
              <button
                key={index}
                onClick={() => setCategory(cat)}
                className={`px-4 py-2 rounded-full text-sm font-medium transition duration-300 ${
                  category === cat
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                {cat}
              </button>
            ))}
          </div>
        </ScrollReveal>

        {/* Projects Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredProjects.map((project, index) => (
            <ScrollReveal key={project.id} delay={index * 150} direction={index % 2 === 0 ? 'left' : 'right'}>
              <ProjectCard project={project} />
            </ScrollReveal>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
