import React, { useEffect, useRef } from 'react';

const Hero = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let particlesArray = [];
    let animationFrameId;
    
    // Colors for dark theme
    const colors = ['#2563eb', '#4f46e5', '#7c3aed', '#8b5cf6'];
    
    // Set canvas size
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      initParticles(); // Reinitialize particles when resizing
    };
    
    window.addEventListener('resize', handleResize);
    handleResize();
    
    // Particle class
    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 2 + 0.5; // Smaller particles for subtlety
        this.color = colors[Math.floor(Math.random() * colors.length)];
        this.speedX = Math.random() * 0.3 - 0.15;
        this.speedY = Math.random() * 0.3 - 0.15;
        this.opacity = Math.random() * 0.5 + 0.1;
      }
      
      update() {
        // Gentle movement
        this.x += this.speedX;
        this.y += this.speedY;
        
        // Wrap around edges
        if (this.x < 0) this.x = canvas.width;
        if (this.x > canvas.width) this.x = 0;
        if (this.y < 0) this.y = canvas.height;
        if (this.y > canvas.height) this.y = 0;
      }
      
      draw() {
        ctx.fillStyle = this.color;
        ctx.globalAlpha = this.opacity;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.closePath();
        ctx.fill();
      }
    }
    
    // Create particles
    function initParticles() {
      particlesArray = [];
      // Adjust number of particles based on screen size
      const particleCount = Math.min(Math.floor((canvas.width * canvas.height) / 15000), 100);
      for (let i = 0; i < particleCount; i++) {
        particlesArray.push(new Particle());
      }
    }
    
    // Connect particles with lines if they're close
    function connectParticles() {
      const maxDistance = canvas.width > 768 ? 150 : 100;
      for (let a = 0; a < particlesArray.length; a++) {
        for (let b = a; b < particlesArray.length; b++) {
          const dx = particlesArray[a].x - particlesArray[b].x;
          const dy = particlesArray[a].y - particlesArray[b].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          
          if (distance < maxDistance) {
            // More subtle connections
            const opacity = 0.2 * (1 - distance / maxDistance);
            ctx.strokeStyle = `rgba(99, 102, 241, ${opacity})`;
            ctx.lineWidth = 0.5;
            ctx.beginPath();
            ctx.moveTo(particlesArray[a].x, particlesArray[a].y);
            ctx.lineTo(particlesArray[b].x, particlesArray[b].y);
            ctx.stroke();
          }
        }
      }
    }
    
    // Animation loop
    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Draw particles and connections
      for (let i = 0; i < particlesArray.length; i++) {
        particlesArray[i].update();
        particlesArray[i].draw();
      }
      
      connectParticles();
      
      animationFrameId = requestAnimationFrame(animate);
    }
    
    initParticles();
    animate();
    
    // Clean up
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return (
    <section id="home" className="relative flex flex-col md:flex-row items-center justify-between py-16 px-6 md:px-12 lg:px-24 bg-gradient-to-r from-gray-900 to-indigo-950 min-h-screen overflow-hidden">
      {/* Dark theme canvas background */}
      <canvas
        ref={canvasRef}
        className="absolute inset-0 w-full h-full z-0"
        style={{ opacity: 0.7 }}
      />

      {/* Content (with z-index to place above animation) */}
      <div className="w-full md:w-1/2 mb-10 md:mb-0 z-10">
        <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-4">
          Hi, I'm <span className="text-indigo-400">Subashini S</span>
        </h1>
        <h2 className="text-2xl md:text-3xl font-semibold text-indigo-200 mb-6">
          Aspiring Software Engineer
        </h2>
        <p className="text-lg text-gray-300 mb-8 max-w-lg">
          I create scalable applications with Java, Spring Boot, React, and MongoDB.
          Passionate about optimizing backend performance and enhancing user experience.
        </p>
        <div className="flex flex-wrap gap-4">
          <a 
            href="#contact" 
            className="px-6 py-3 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition duration-300 transform hover:-translate-y-1"
          >
            Contact Me
          </a>
          <a 
            href="#projects" 
            className="px-6 py-3 border border-indigo-400 text-indigo-200 rounded-md hover:bg-indigo-900 hover:border-indigo-300 transition duration-300 transform hover:-translate-y-1"
          >
            View Projects
          </a>
        </div>
      </div>
      <div className="w-full md:w-1/2 flex justify-center z-10">
        <div className="relative w-64 h-64 md:w-80 md:h-80 rounded-full overflow-hidden border-4 border-indigo-400 shadow-xl shadow-indigo-500/20">
          <img 
            src="/assets/images/profile.jpg" 
            alt="Subashini S" 
            className="absolute inset-0 w-full h-full object-cover"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = 'https://via.placeholder.com/300?text=Subashini+S';
            }}
          />
        </div>
      </div>
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce z-10">
        <a href="#about" className="text-indigo-300 hover:text-white">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
          </svg>
        </a>
      </div>
    </section>
  );
};

export default Hero;
